import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UsersService } from 'src/app/services/users.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-update-user',
  templateUrl: './update-user.component.html',
  styles: [
  ]
})
export class UpdateUserComponent {
  ID:any;
  student:any;
  // @Input() student:any;
  constructor(myRoute:ActivatedRoute,public UService: UsersService, public router:Router){
    this.ID = myRoute.snapshot.params["id"];

    console.log(myRoute.snapshot.params["id"]);


  }
  ngOnInit(): void {

    this.UService.GetUserByID(this.ID).subscribe({
      next:(data)=>{
        // console.log(data)
        this.student = data;
      },
      error:(err)=>{console.log(err)}
    });
  }
  update(name:any, email:any, phone:any, city:any, grade:any){
    let newStudent = {name, email, phone, city, grade};
    this.UService.UpdateUser( this.ID,newStudent).subscribe();
    this.router.navigateByUrl("/")
  }
}
