import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UsersService } from 'src/app/services/users.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styles: [
  ]
})
export class DeleteUserComponent {
  ID:any;
   constructor(myRoute:ActivatedRoute,public UService:UsersService ,public router:Router){
     this.ID = myRoute.snapshot.params["id"];
    //  console.log(myRoute.snapshot.params["id"]);

  }
  delItem(){
     this.UService.  DeleteUserByID(this.ID).subscribe({ });
     this.router.navigateByUrl("/users")
    }
}

<div class="alert alert-primary text-center fs-3" role="alert" >
    Delete?
    <div class="d-flex justify-content-evenly">
        <div>
          <a class="btn btn-outline-dark" (click)="delItem()">YES</a>
        </div>
        <div>
          <a class="btn btn-outline-success" routerLink="/users">NO</a>
        </div>
      </div>
</div>
