import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { StudentsService } from 'src/app/services/users.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styles: [
  ]
})
export class DeleteUserComponent {
  ID:any;
   constructor(myRoute:ActivatedRoute,public SService:StudentsService ,public router:Router){
     this.ID = myRoute.snapshot.params["id"];
    //  console.log(myRoute.snapshot.params["id"]);

  }
  delItem(){

     this.SService. DeleteStudentByID(this.ID).subscribe({ });
     this.router.navigateByUrl("/")
    }
}
