import { Component } from '@angular/core';
import { Router } from '@angular/router';
import {UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(public UService: UsersService, public router:Router){}

  add(name:any, email:any, phone:any, street:any,suite:any, city:any ){
    let address={street,suite,city};
    let newUser= {name, email, phone, address};
    this.UService. AddUser(newUser).subscribe();
    this.router.navigateByUrl("/users")
  }

  data={}

  @Output() myEvent=new EventEmitter();
    myValidation = new FormGroup({
    name: new FormControl("",[Validators.required , Validators.minLength(3)]),
    age:new FormControl("",[Validators.required,Validators.max(40), Validators.min(20)]),
    mail: new FormControl("",[Validators.required ,Validators.pattern(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/)])
  })
get AgeValid(){
  if((this.myValidation.controls["age"].value!=="")){

    return ((this.myValidation.controls["age"].valid))

  }
   return true
}
get NameValid(){
  if((this.myValidation.controls["name"].value!=="")){

    return ((this.myValidation.controls["name"].valid))

  }
   return true
}
get MailValid(){
  if((this.myValidation.controls["mail"].value!=="")){

    return ((this.myValidation.controls["mail"].valid))
  }
   return true

}


  add(){
    this.data={name:this.myValidation.controls.name.value , age:this.myValidation.controls.age.value, mail:this.myValidation.controls.mail.value};
    if(this.myValidation.valid){
      this.myEvent.emit(this.data)
    }

  }


}
