import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class UsersService {
  constructor(private myClient:HttpClient) { }

  private Base_URL = "http://localhost:3000/students";

  //2-All Requests
  GetAllStudents(){
    // console.log(this.myClient.get(this.Base_URL));

    return this.myClient.get(this.Base_URL);
  }
  GetStudentByID(id:any){
    return this.myClient.get(this.Base_URL+"/"+id);
  }
  AddStudent(newStudent:any){
    return this.myClient.post(this.Base_URL,newStudent);
  }
  UpdateStudent(id:any, newStudent:any){
    return this.myClient.put(this.Base_URL+"/"+id, newStudent);
  }
  DeleteStudentByID(id:any){
return  this.myClient.delete(this.Base_URL+"/"+id)
  }

}
